import { createStore } from "redux";

const initialState = {
  counter: 0,
  showCounter: true,
};
const reducer = (state = initialState, actions) => {
  if (actions.type === "ADD") {
    if (actions.payload) {
      return {
        counter: state.counter + actions.payload,
        showCounter: state.showCounter,
      };
    }
    return {
      counter: state.counter + 1,
      showCounter: state.showCounter,
    };
  } else if (actions.type === "REMOVE") {
    return {
      counter: state.counter - 1,
      showCounter: state.showCounter,
    };
  }
  if (actions.type === "TOGGLE") {
    const isCounterShown = state.showCounter;
    return {
      ...state,
      showCounter: !isCounterShown,
    };
  }
  return state;
};
const store = createStore(reducer);

export default store;
